import time
import ctypes
from ctypes import wintypes
import tkinter as tk
from tkinter import filedialog, messagebox, ttk
import os
import psutil
from PIL import Image, ImageTk
import base64
from io import BytesIO

# 定义必要的 Win32 API 函数
user32 = ctypes.WinDLL('user32', use_last_error=True)

def enum_windows_proc(hwnd, lParam):
    if user32.IsWindowVisible(hwnd):
        length = user32.GetWindowTextLengthW(hwnd)
        buff = ctypes.create_unicode_buffer(length + 1)
        user32.GetWindowTextW(hwnd, buff, length + 1)
        pid = wintypes.DWORD()
        user32.GetWindowThreadProcessId(hwnd, ctypes.byref(pid))
        if pid.value in hwnds:
            hwnds[pid.value] = hwnd
    return True

def find_window_by_process_name(process_name):
    global hwnds
    hwnds = {}
    for proc in psutil.process_iter(['pid', 'name']):
        if proc.info['name'].lower() == process_name.lower():
            hwnds[proc.info['pid']] = None
    EnumWindowsProc = ctypes.WINFUNCTYPE(wintypes.BOOL, wintypes.HWND, wintypes.LPARAM)
    user32.EnumWindows(EnumWindowsProc(enum_windows_proc), 0)
    for hwnd in hwnds.values():
        if hwnd:
            return hwnd
    return None

def move_window(hwnd, width, height):
    if hwnd:
        screen_width = user32.GetSystemMetrics(0)
        screen_height = user32.GetSystemMetrics(1)
        x = (screen_width - width) // 2
        y = (screen_height - height) // 2
        user32.MoveWindow(hwnd, x, y, width, height, True)
    else:
        messagebox.showerror("错误", "找不到窗口")

def select_game():
    global game_path, game_name
    game_path = filedialog.askopenfilename(title="选择游戏文件", filetypes=[("可执行文件", "*.exe")])
    if not game_path:
        return

    game_name = os.path.basename(game_path)
    messagebox.showinfo("提示", f"提示\n\n已选择游戏文件：{game_path}\n\n请运行游戏，游戏运行后将会自动修改窗口大小")

    # 等待用户启动游戏
    hwnd = None
    while not hwnd:
        hwnd = find_window_by_process_name(game_name)
        time.sleep(1)

    # 获取用户选择的分辨率
    resolution = resolution_var.get().split()[0]  # 只提取分辨率部分
    width, height = map(int, resolution.split('x'))

    # 调整窗口大小和位置
    move_window(hwnd, width, height)

    # 创建置顶的弹窗
    top = tk.Toplevel()
    top.title("提示")
    top.geometry("300x100")
    top.attributes("-topmost", True)
    top.iconphoto(False, icon)  # 设置弹窗图标
    tk.Label(top, text=f"窗口已调整到 {width}x{height} 分辨率。", padx=20, pady=20).pack()
    tk.Button(top, text="确定", command=top.destroy).pack(pady=10)

# 创建 GUI
root = tk.Tk()

# 设置软件图标
icon_base64 = ""  # 在此处填写base64内容
icon_data = base64.b64decode(icon_base64)
img = Image.open(BytesIO(icon_data))
img = img.resize((32, 32), Image.LANCZOS)
icon = ImageTk.PhotoImage(img)
root.iconphoto(True, icon)  # 确保在任务栏显示图标

# 设置窗口标题和图标
root.title("GBT窗口分辨率调整工具")
root.iconphoto(False, icon)  # 设置窗口图标

# 禁用放大和缩小按钮
root.resizable(False, False)

# 隐藏最小化和最大化按钮
root.overrideredirect(True)
root.update_idletasks()
root.overrideredirect(False)
root.attributes('-toolwindow', True)

# 分辨率选项
resolutions = [
    "640x480 4:3", "800x600 4:3", "1024x768 4:3", "1280x960 4:3", "1400x1050 4:3", "1600x1200 4:3", "1920x1440 4:3", "2048x1536 4:3",  # 4:3
    "854x480 16:9", "1280x720 16:9", "1366x768 16:9", "1600x900 16:9", "1920x1080 16:9", "2560x1440 16:9", "3840x2160 16:9",  # 16:9
    "1280x800 16:10", "1440x900 16:10", "1680x1050 16:10", "1920x1200 16:10", "2560x1600 16:10", "3840x2400 16:10",  # 16:10
    "2560x1080 21:9", "3440x1440 21:9", "3840x1600 21:9", "5120x2160 21:9",  # 21:9
    "3840x1080 32:9", "5120x1440 32:9"  # 32:9
]

resolution_var = tk.StringVar(value="640x480 4:3")
tk.Label(root, text="选择分辨率:").pack(pady=5)
resolution_menu = ttk.Combobox(root, textvariable=resolution_var, values=resolutions)
resolution_menu.pack(pady=5)

tk.Button(root, text="选择游戏文件", command=select_game).pack(pady=10)

root.mainloop()
